package day02;

public class Cast_Test {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i=1234;
		int k;
		float f =10.5f;
		double d =100.55;
		
	
		k=(int)f;
		System.out.println(k);
		
		f=i;
		System.out.println(f);
		
		f=(float)d;
		System.out.println(f);

	}

}
